import { useState } from 'react';
import './App.css';

function App() {
  const [todo, setTodo] = useState({})
  const [todos, setTodos] = useState([])

  const iptChangeEventHandler = (e) => {
    setTodo({ todo : e.target.value})
  }
  const btnClickEventHandler = (e) => {
    console.log("배열",todos)
    console.log("넣을값",todo)
    console.log("넣은 후", [...todos, todo])
    //setTodos([...todos, todo])
  }
  return (
    <div className="App">
      <div><input onChange={iptChangeEventHandler}></input><button onClick={btnClickEventHandler}>추가하기</button></div>
      <div><h2>Todo List</h2></div>
      <div>
        <div className="TodoCard"></div>
      </div>
    </div>
  );
}

export default App;
