import { useState } from 'react';
import './App.css';

function App() {
  const [todo, setTodo] = useState({})
  const [todos, setTodos] = useState([])

  const iptChangeEventHandler = (e) => {
    setTodo({ title : e.target.value})
    setTodo({ title : ''})
  }
  const btnClickEventHandler = (e) => {
    setTodos([...todos, todo])
  }

  return (
    <div className="App">
      <div><input onChange={iptChangeEventHandler} value={todo.title}></input><button onClick={btnClickEventHandler}>추가하기</button></div>
      <div><h2>Todo List</h2></div>
      <div>
      {todos.map((todo)=>{
        return (
          <div className="TodoCard">{todo.title}</div>
        )
      })}

      </div>
    </div>
  );
}

export default App;
